import React, { useState, useEffect } from 'react';
import { useNavigate,useLocation } from 'react-router-dom';
function ViewAnnouncement() {
    const [announcements, setAnnouncements] = useState([]);
    const location = useLocation();
    const groupId = location.state?.groupId; 
    console.log("groupid",groupId);
    useEffect(() => {
        fetch(`http://localhost:5001/viewAnnouncement/${groupId}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                setAnnouncements(data);
            })
            .catch(error => console.error('Error fetching announcements:', error));
    }, [groupId]);
    const handleView = async (videoId) => {
        navigate(`/Video/${videoId}`)
    };
    return (
        <div>
            <h2>Announcements</h2>
            <ul>
                {announcements.map((announcement, index) => (
                    <li key={index}>
                        <h3>{announcement.title}</h3>
                        <p>{announcement.detail}</p>
                        <small>Posted by: {announcement.announcer}</small>
                        {announcement.attached_post && (
                            <p>Attached Post ID: {announcement.attached_post}</p>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ViewAnnouncement;
