import React, { useState } from "react";

function LoginForm() {
    const [showPassword, setShowPassword] = useState(false);
    const [action, setAction] = useState("Login");
    const [email, setEmail] = useState("");
    const [name, setName] = useState("");
    const [role, setRole] = useState("");
    const [emailError, setEmailError] = useState(false);

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    const updateUI = () => {
        const headerText = document.querySelector('.header .text');
        const submitContainer = document.querySelector('.submit-container');
        const nameInputContainer = document.getElementById('nameInputContainer');
        const forgotPassword = document.getElementById('forgotPassword'); // Get the element by ID
        const roleInputContainer = document.getElementById('roleInputContainer');
        const signUpButton = document.getElementById('signUpButton');
        const loginButton = document.getElementById('loginButton');
        const tooltipText = document.getElementById('switchTooltip');
        // 设置按钮的初始样式为紫色
        if (signUpButton) {
            signUpButton.classList.remove('gray');
        }
        if (loginButton) {
            loginButton.classList.remove('gray');
        }
        //如果在login界面则不显示nameinput和signup button,当鼠标移到“交换”标志时会显示"Switch to Sign Up"
        if (action === "Login") {
            headerText.textContent = "Vup";
            nameInputContainer.style.display = "none";
            forgotPassword.style.display = "block";
            signUpButton.style.display = "none";
            loginButton.style.display = "block";
            roleInputContainer.style.display = "none";
            tooltipText.textContent = "Switch to Sign Up";
            //如果在signup界面则不显示forgotpassword button和login button,当鼠标移到“交换”标志时会显示"Switch to Log in"
        } else if (action === "Sign Up") {
            headerText.textContent = "Vup";
            nameInputContainer.style.display = "block";
            forgotPassword.style.display = "none";
            signUpButton.style.display = "block";
            loginButton.style.display = "none";
            tooltipText.textContent = "Switch to Log in";
            roleInputContainer.style.display = "block";
        }
        document.getElementById('actionInput').value = action;
    };

    const validateEmail = () => {
        // Implement the email validation logic
    };

    const validateRole = () => {
        // Implement the role validation logic
    };

    const toggleAction = () => {
        // Implement the logic to toggle between Login and Sign Up
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();
        if (validateEmail() && validateRole()) {
            // Implement the form submission logic
        } else {
            alert("Please check your inputs.");
        }
    };

    return (
        <div>
            <form id="myform" method="post" onSubmit={handleFormSubmit}>
                <input type="hidden" name="action" id="actionInput" value={action} />
                <div className="container">
                    <div className='header'>
                        <div className='text'></div>
                        <div className='underline'></div>
                    </div>
                    <div className='inputs'>
                        <div className='input' id="nameInputContainer">
                            <div className="input-content">
                                <img src="image/person.png" alt="User Icon" />
                                <input
                                    type="text"
                                    placeholder="Name"
                                    name="user_name"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className='input'>
                            <img src="image/email.png" alt="Email Icon" />
                            <input
                                type="email"
                                placeholder="Email"
                                id="emailInput"
                                name="user_email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                onInput={validateEmail}
                            />
                            {emailError && <span className="email-tooltiptext" id="emailTooltip">Invalid format</span>}
                        </div>
                        <div className='input'>
                            <img src="image/password.png" alt="Password Icon" />
                            <input
                                type={showPassword ? "text" : "password"}
                                placeholder="Password"
                                id="passwordInput"
                                autoComplete="new-password"
                                name="user_pass"
                            />
                            <input
                                type="text"
                                id="hiddenPasswordInput"
                                style={{ opacity: 0, position: "absolute", top: -9999, left: -9999 }}
                            />
                            <p className="password-toggle" onClick={togglePasswordVisibility}>
                                {showPassword ? "Hide" : "Show"}
                            </p>
                        </div>
                    </div>
                    <div className="roleChoose" id="roleInputContainer">
                        <label htmlFor="role" style={{ fontSize: "1.17em", fontWeight: "bold", color: "#4c00b4" }}>
                            <h3>&nbsp;&nbsp;*Select your role:&nbsp;</h3>
                        </label>
                        <select
                            name="role"
                            id="role"
                            style={{ fontSize: "18px", padding: "5px 10px", borderRadius: "10px", width: "185px", border: "1px solid black" }}
                            value={role}
                            onChange={(e) => setRole(e.target.value)}
                        >
                            <option value="empty" disabled hidden>Select a role</option>
                            <option value="sender">Sender</option>
                            <option value="receiver">Receiver</option>
                        </select>
                    </div>
                    <div className="forgot-password" onClick={navigateToForgotPasswordPage} id="forgotPassword">
                        Forgot password? <span>Click Here!</span>
                    </div>
                    <div className="tooltip" style={{ display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", margin: 0, padding: 0 }}>
                        <img
                            src="image/exchange.png"
                            alt="Switch"
                            className="switch-icon"
                            style={{ margin: "2px 0", width: "40px", height: "40px" }}
                            onClick={toggleAction}
                        />
                        <span className="tooltiptext" id="switchTooltip" style={{ textAlign: "center", marginTop: "2px", marginBottom: "2px" }}>
                            Switch between Login and Sign Up interface
                        </span>
                    </div>
                    <div className="submit-container">
                        <button className={action === "Login" ? "submit" : "submit gray"} type="submit">
                            Submit
                        </button>
                    </div>
                </div>
            </form>
        </div>


    );
}

export default LoginForm;
