import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function GroupPostsPage() {
    const { groupId } = useParams();
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        // Fetch all posts for the group
        fetch(`http://localhost:5001/all-group-post/${groupId}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                setPosts(data);
            })
            .catch(error => {
                console.error('Error fetching group posts:', error);
            });
    }, [groupId]);

    return (
        <div id="content">
                <div id="VideoList">
                    <table className="videoTable">
                        <thead>
                            <tr>
                                <td className="all" colSpan='4'>All Videos</td>
                            </tr>
                        </thead>
                        <tbody>
                            {posts.map((post) => (
                                <tr key={post.post_id} onClick={() => handleView(post.post_id)} style={{cursor: 'pointer'}}>
                                    <td className="title" data-description={post.post_text}>
                                        {post.post_title}
                                    </td>
                                    <td>{showdate(post.post_date)}</td>
                                    <td><button className='view' onClick={() => {
                                        handleView(post.post_id)
                                    }}>View</button></td>
                                    <td>
                                        {userId == adminid && (
                                            <button className='editButton' onClick={(e) => {
                                            e.stopPropagation();
                                            setSelectedPost(post);
                                            setShowModal(true);
                                        }}>Delete Post</button>                                        
                                        )}
                                        {renderEditForm()}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
    );
}

export default GroupPostsPage;
