import React, { useState, useEffect } from 'react';
import { useNavigate,useLocation } from 'react-router-dom';
import '../component/CSS/viewannouncement.css';
function ViewAnnouncement() {
    const navigate = useNavigate();
    const [announcements, setAnnouncements] = useState([]);
    const location = useLocation();
    const groupId = location.state?.groupId; 
    console.log("groupid",groupId);
    useEffect(() => {
        fetch(`http://localhost:5001/viewAnnouncement/${groupId}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                setAnnouncements(data);
            })
            .catch(error => console.error('Error fetching announcements:', error));
    }, [groupId]);
    const handleView = async (videoId) => {
        navigate(`/Video/${videoId}`)
    };
    return (
        <div id='announce'>
            <h2>Announcements</h2>
    {announcements.map((announcement, index) => (
        <table key={index} className='announcetab'>
            <tbody>
                <tr className='announcetitle'>

                    <td>{announcement.title}</td>
                </tr>
                <tr>

                    <td>{announcement.detail}</td>
                </tr>
                <tr>
                    <td>
                        {announcement.attached_post ?  <b>Post:</b>
                        (

                            <span 
                                onClick={() => handleView(announcement.attached_post)}>

                                    Click to view Attached Post
                            </span>
                        ) : ""}
                    </td>
                </tr>
            </tbody>
        </table>
    ))}
        </div>
    );
}

export default ViewAnnouncement;
