import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import GroupCard from './GroupCard';

function MainContent() {
  const [userId, setUserId] = useState('');
  const [groups, setGroups] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const sessionUser = sessionStorage.getItem('user');
    if (!sessionUser) {
      navigate('/login');
    } else {
      const user = JSON.parse(sessionUser);
      setUserId(user.userid);
      console.log("User Id: " + user.userid);
    }
  }, []);

  useEffect(() => {
    if (userId) {
      fetch(`http://localhost:5001/groups/${userId}`)
        .then(response => {
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          return response.json();
        })
        .then(data => {
          // Fetch member count for each group
          const groupsWithCountsPromises = data.map(group =>
            fetch(`http://localhost:5001/groups/count/${group.groupid}`)
              .then(response => response.json())
              .then(countData => {
                return { ...group, memberCount: countData.memberCount };
              })
          );

          Promise.all(groupsWithCountsPromises)
            .then(completedGroups => {
              setGroups(completedGroups);
            });
        })
        .catch(error => {
          console.error('Error fetching groups:', error);
        });
    }
  }, [userId]);
  console.log(groups);
  

  return (
    <div className="content">
      <div className="main-content">
        <div className="first-row">
          {
            groups.map(group => (
              <GroupCard
                key={group.groupid}
                imgUrl={group.imgUrl}
                time={group.group_creation_time}
                groupName={group.groupname}
                admin={group.admin}
                numberOfPeople={group.memberCount}
              />
            ))
          }

        </div>

      </div>
    </div>
  );
}

export default MainContent;
