import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
function VideoPage() {
    const [videoUrl, setVideoUrl] = useState('');
    const { videoId } = useParams(); // Extract videoId from the URL

    useEffect(() => {
        // Function to fetch the signed URL
        const fetchVideoUrl = async () => {
            try {
                const response = await fetch(`http://localhost:5001/get-video-url/${videoId}`);
                const data = await response.json();
                if (response.ok) {
                    setVideoUrl(data.signedUrl); // Set the fetched URL
                } else {
                    console.error('Failed to fetch video URL:', data.error || 'Unknown error');
                }
            } catch (error) {
                console.error('Error fetching video URL:', error);
            }
        };

        fetchVideoUrl();
    }, [videoId]); // Effect runs when videoId changes

    return (
        <div className="video-container">
            {videoUrl ? (
                <>
                    <video width="640" height="480" controls>
                        <source src={videoUrl} type="video/mp4" />
                        Your browser does not support the video tag.
                    </video>
                    <br />
                    <a href={videoUrl} download={`video_${videoId}.mp4`}>Download Video</a>
                </>
            ) : (
                <p>Loading video...</p>
            )}
        </div>
    );
}

export default VideoPage;
