import React, { useState, useEffect } from 'react';
import "../component/CSS/recorded.css";
import "../component/CSS/sidebar_style.css";

function RecordedPage() {
    const [userId, setUserId] = useState('21');
    const [group, setGroup] = useState('all');
    const [arrangement, setArrangement] = useState('date');
    const [posts, setPosts] = useState([]);
    const [editingPostId, setEditingPostId] = useState(null);
    const [newTitle, setNewTitle] = useState('');
    const [newText, setNewText] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        fetch(`http://localhost:5000/get-posts/${userId}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                console.log(userId);
                console.log('Fetched data:', data); 
                setPosts(data);
            })
            .catch(error => {
                console.error('Error fetching posts:', error);
            });
    }, [userId]);
    

    const handleDelete = (postId) => {
        // Confirmation dialog
        const isConfirmed = window.confirm("Are you sure you want to delete this post?");
        if (!isConfirmed) {
            return; // If the user clicks 'Cancel', do nothing
        }
    
        // Proceed with deletion if confirmed
        fetch(`http://localhost:5000/delete-posts/${postId}`, { method: 'DELETE' })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Error in deleting post');
                }
                return response.text();
            })
            .then(() => {
                // Update the posts state to remove the deleted post
                setPosts(prevPosts => prevPosts.filter(post => post.post_id !== postId));
    
                // Show success message
                window.alert("Post deleted successfully");
            })
            .catch(error => console.error('Error:', error));
    };

    // Modify handleEdit to use the state values
    const handleEdit = (postId) => {
        const postData = { title: newTitle, text: newText };
        fetch(`http://localhost:5000/edit-posts/${postId}`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(postData)
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Error in editing post');
            }
            return response.json();
        })
        .then(() => {
            setPosts(posts.map(post => post.post_id === postId ? { ...post, post_title: newTitle, post_text: newText } : post));
            // Reset editing state
            setEditingPostId(null);
            setNewTitle('');
            setNewText('');
        })
        .catch(error => console.error('Error:', error));
    };


    // const renderEditForm = () => {
    //     if (showModal) {
    //         return (
    //             <div className="modal-backdrop" onClick={() => setShowModal(false)}>
    //                 <div className="modal-content" onClick={e => e.stopPropagation()}>
    //                     <div className="close-button" onClick={() => setShowModal(false)}>&times;</div>
    //                     <h2>Edit Post</h2>
    //                     <h3>Change Title:</h3>
    //                     <input 
    //                         type="text" 
    //                         value={newTitle} 
    //                         onChange={(e) => setNewTitle(e.target.value)} 
    //                         placeholder="New title" 
    //                         className="modal-input"
    //                     />
    //                     <h3>Change Description</h3>
    //                     <input 
    //                         type="text" 
    //                         value={newText} 
    //                         onChange={(e) => setNewText(e.target.value)} 
    //                         placeholder="New text" 
    //                         className="modal-input"
    //                     />
    //                     <button onClick={() => handleEdit(editingPostId)}>Save</button>
    //                 </div>
    //             </div>
    //         );
    //     }
    //     return null;
    // };
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toISOString().split('T')[0]; // Formats to 'month/day/year'. Adjust the locale and options as needed.
    };
    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);
    };

    return (
        <>
            <div id="condition">
                <table>
                    <tbody>
                        <tr>
                            <td>
                                <h3>Group</h3>
                            </td>
                            <td>
                                <h3>Arrange By</h3>
                            </td>
                        </tr>
                        <tr>
                            <td>
                                <select name="group" value={group} onChange={(e) => setGroup(e.target.value)}>
                                    <option value="all">All</option>
                                    <option value="group1">Group 1</option>
                                    <option value="group2">Group 2</option>
                                </select>
                            </td>
                            <td>
                                <select name="arrange" value={arrangement} onChange={(e) => setArrangement(e.target.value)}>
                                    <option value="date">Date</option>
                                    <option value="asc">Name Ascending</option>
                                    <option value="des">Name Descending</option>
                                </select>
                            </td>
                            <td>
                                <button type="submit" name="apply" id="apply">Apply</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
            <div id="VideoList">
                <table id="videoTable">
                    <thead>
                        <tr>
                            <td className="title">All Videos</td>
                        </tr>
                    </thead>
                    <tbody>
                        {posts.map((post) => (
                            <tr key={post.post_id}>
                                <td className="title" data-description={post.post_text}>
                                    {post.post_title}
                                </td>
                                <td>{formatDate(post.post_date)}</td>
                                <td>
                                    <button className='editButton' onClick={() => {
                                    setEditingPostId(post.post_id);
                                    setNewTitle(post.post_title);
                                    setNewText(post.post_text);
                                    toggleModal;
                                    }}>Edit</button>
                                    <button className='deleteButton' onClick={() => handleDelete(post.post_id)}>Delete</button>
                                    {isModalOpen && (
                <div className="modal-backdrop">
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <div className="close-button" onClick={toggleModal}>&times;</div>
                        <h2>Edit Post</h2>
                        {/* Add your form fields and buttons here */}
                        <button onClick={toggleModal}>Close</button>
                    </div>
                </div>
            )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
}

export default RecordedPage;
